import { observable, action, computed } from 'mobx';
import axios from 'axios';


class HomeworkStore {
  @observable
  sido = "default";

  @action
  setSido = (e, data) => {
    this.sido = e.target.value
  }

  @observable
  sigungu = "default";

  @action
  setSigungu = (e, data) => {
    this.sigungu = e.target.value
  }

  @observable
  bjdong = "default";

  @action
  setBjdong = (e, data) => {
    this.bjdong = e.target.value
  }

  @observable
  platGb = "default";

  @action
  setPlatGb = (e, data) => {
    this.platGb = e.target.value
  }

  @observable
  _rowData = new Array();

  @action
  setRowData = (data) => {
    this._rowData = data
  }

  @computed
  get rowData() {
    return this._rowData
  }

  @observable
  loginId = ""

  @action
  setLoginId = (e, data) => {
    console.log("## setLoginId in store")
    this.loginId = e.target.value
  }

  @observable
  loginPw = ""

  @action
  setLoginPw = (e, data) => {
    console.log("## setLoginPw in store", e.target.value)
    this.loginPw = e.target.value
  }

  @observable
  searching = ""

  @observable
  loginSession = false

  @observable
  dataCount = ""

  @action
  logOut = () => {
    console.log("## logOut in store")
    this.loginSession = false
    this.loginId = ""
    this.loginPw = ""
  }

  @action
  clearRowData = () => {
    console.log("## clearRowData in store")
    this._rowData = new Array();
    this.sido = "default"
    this.sigungu = "default"
    this.bjdong = "default"
    this.platGb = "default"
    this.loginId = ""
    this.loginPw = ""
  }

  @action
  apiUserLogin = () => {
    console.log("#### apiUserLogin in store")
    let params = new Object()
    params["userId"] = this.loginId
    params["pw"] = this.loginPw

    return axios.post(`http://localhost:8080/user/login`, params).then(response => {
      if (response.data == "ok") {
        this.loginSession = true
      } else {
        alert("로그인 실패했습니다.")
      }
    })

  }

  @action
  apiUserRegister = () => {
    console.log("#### apiUserRegister in store")
    let params = new Object()
    params["userId"] = this.loginId
    params["pw"] = this.loginPw

    return axios.post(`http://localhost:8080/user/login`, params).then(response => {
      if (response.data == "ok") {
        this.loginSession = true
        alert("회원가입 되였습니다.")
      } else {
        alert("이미 동일한 ID가 존재합니다.")
      }
    })
  }

  @action
  apiCallForData = () => {

  }











}
export default HomeworkStore;
